import React from 'react'
import AddTodoModal from './AddTodoModal.jsx';
import moment from 'moment/moment';
import { deleteTodoApi } from '../../services/api.js';
import { toast } from 'react-toastify';

function Todo({ todo, setRefreshList }) {

    const handleDelete = async () => {
        const result = await deleteTodoApi({
            todo_Id: todo._id
        });

        if (result.data.status === 200) {
            setRefreshList(new Date());
            toast('Todo deleted successfully');
        } else {
            toast('Failed to delete todo, please try again');
        }
    };

    return (
        <div className='col-sm-3 mx-3 my-2 alert bg-light'>
            <div className="card-header">
                {
                    todo.isCompleted ? 'Completed' : 'Not Completed'
                }
            </div>
            <hr></hr>
            <div className="card-body">
                <h4 className='card-title'>{todo.desc}</h4>
                <p className='card-text'>{moment(todo.date).fromNow()}</p>

            </div>
            <hr style={{ border: '0px' }}></hr>
            <div className="actionButtons" style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                <div className="deleteButton" >
                    <button style={{ background: 'lightred' }} onClick={handleDelete} >Delete</button>
                </div>

                <div className="markTodo">
                    <button style={{ background: 'green' }}>{todo.isCompleted ? 'Mark Uncomplete' : 'Mark Completed'}</button>
                </div>
            </div>
        </div>
    )
}

export default Todo;
